        <p>Escanea el c√≥digo para chatear desde tu m√≥vil:</p>
        <img src="./assets/imagenes/imagens/whatsappqr.jpeg" alt="C√≥digo QR de WhatsApp" id="whatsapp-qr">
        
        <a id="whatsapp-web-link" 
           href="https://wa.me/5491124985882?text=Quiero%20hacer%20una%20consulta%20por%20la%20web" 
           target="_blank" 
           class="qr-action-button">
            Abrir WhatsApp Web
        </a>
    </div>

    <div id="whatsappBadge" class="whatsapp-badge">
        <span class="badge-icon">üëã</span>
        ¬°Hola! ¬øPodemos ayudarte en algo?
    </div>

    <a id="whatsappLink" 
       href="https://wa.me/5491124985882?text=Quiero%20hacer%20una%20consulta%20por%20la%20web" 
       class="whatsapp-button" 
       target="_blank">
        <span class="new-message-indicator"></span> 
        <img src="./assets/formularios/formulario-whatsapp/icone-whatsapp.svg" style="width: 40px; filter: invert(0);">
    </a>
</div>
            
<script>
document.addEventListener('DOMContentLoaded', function() {
    
    // 1. Definimos las variables con seguridad
    var whatsappLink = document.getElementById('whatsappLink');
    var qrPopup = document.getElementById('qr-popup');
    var whatsappWebLink = document.getElementById('whatsapp-web-link');
    var whatsappBadge = document.getElementById('whatsappBadge');
    
    // Si no existe el bot√≥n principal, salimos para no causar errores
    if (!whatsappLink) return;

    // Inicializamos DataLayer por si no existe
    window.dataLayer = window.dataLayer || [];

    // Aseguramos que el popup arranque oculto
    if (qrPopup) {
        qrPopup.classList.add('qr-popup-hidden');
        qrPopup.style.display = 'none'; // Doble seguridad
    }

    // --- EVENTO CLIC PRINCIPAL ---
    whatsappLink.addEventListener('click', function(e) {
        // Chequeo de ancho de pantalla
        var esEscritorio = window.innerWidth >= 768;

        if (esEscritorio && qrPopup) {
            // >>> MODO ESCRITORIO (PC) <<<
            e.preventDefault(); // Frenamos el enlace SOLO en PC
            
            // L√≥gica de Abrir/Cerrar
            var estaOculto = qrPopup.classList.contains('qr-popup-hidden') || qrPopup.style.display === 'none';

            if (estaOculto) {
                // ABRIR
                qrPopup.classList.remove('qr-popup-hidden');
                qrPopup.style.display = 'block'; 
                
                // Ocultar el globito de "Hola"
                if(whatsappBadge) whatsappBadge.style.display = 'none';

                // Enviar a GTM
                window.dataLayer.push({'event': 'intencion_whatsapp_escritorio'});
                console.log('PC: Popup abierto y evento enviado');

            } else {
                // CERRAR
                qrPopup.classList.add('qr-popup-hidden');
                qrPopup.style.display = 'none';
            }

        } else {
            // >>> MODO M√ìVIL <<<
            // NO usamos preventDefault(), dejamos que vaya a WhatsApp
            
            // Enviar a GTM
            window.dataLayer.push({'event': 'clic_whatsapp_movil'});
            console.log('M√ìVIL: Clic enviado');
        }
    });

    // --- EVENTO CLIC ENLACE TEXTO (Dentro del QR) ---
    if (whatsappWebLink) {
        whatsappWebLink.addEventListener('click', function() {
            window.dataLayer.push({'event': 'clic_whatsapp_web'});
            console.log('WEB: Clic en enlace de texto');
        });
    }

    // --- CERRAR AL HACER CLIC AFUERA (Solo PC) ---
    document.addEventListener('click', function(e) {
        if (!qrPopup || window.innerWidth < 768) return;

        // Si el popup est√° visible...
        if (qrPopup.style.display === 'block' && !qrPopup.classList.contains('qr-popup-hidden')) {
            // Y el clic NO fue en el bot√≥n NI en el popup...
            if (!qrPopup.contains(e.target) && !whatsappLink.contains(e.target)) {
                qrPopup.classList.add('qr-popup-hidden');
                qrPopup.style.display = 'none';
            }
        }
    });

    // --- CERRAR CON TECLA ESCAPE ---
    document.addEventListener('keydown', function(e) {
        if (e.key === 'Escape' && qrPopup) {
            qrPopup.classList.add('qr-popup-hidden');
            qrPopup.style.display = 'none';
        }
    });

});
</script>

<script>
